```c
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

int main() {
    srand(time(NULL));
    int secretNumber = rand() % 100 + 1;
    int userGuess, attempts = 0;

    printf("I've generated a random number between 1 and 100. Guess the number to win!\n");

    while (attempts < 7) {
        scanf("%d", &userGuess);

        if (secretNumber == userGuess) {
            printf("Congratulations! You've guessed the number in %d attempts.\n", ++attempts);
            break;
        } else if (secretNumber < userGuess) {
            printf("Too high! Try again.\n");
        } else if (secretNumber > userGuess) {
            printf("Too low! Try again.\n");
        }
        ++attempts;
    }

    if (attempts == 7) {
        printf("Unfortunately, you didn't guess the number. The secret number was: %d\n", secretNumber);
    }

    return 0;
}
```

